import React, { useState } from "react";
import Drawer from "@mui/material/Drawer";
import Toolbar from "@mui/material/Toolbar";
import List from "@mui/material/List";
import Divider from "@mui/material/Divider";
import ListItemComponent from "./ListItemComponent";
import Box from "@mui/material/Box";
import GridDados from "../Grids/GridDados";
import GridArquivos from "../Grids/GridArquivos";
import "./Sidebar.scss";
export default function DrawerComponent() {
  const [selectedGrid, setSelectedGrid] = useState(null);

  const handleGridSelection = (grid) => {
    setSelectedGrid(grid);
  };
  return (
    <>
      <Drawer
        className="muiDrawer-root & .muiDrawer-paper"
        variant="permanent"
        anchor="left"
      >
        <Toolbar />
        <Divider />
        <List>
          {["Visualizar dados XML", "Visualizar arquivo"].map((text, index) => (
            <ListItemComponent
              key={text}
              text={text}
              index={index}
              onClick={() =>
                handleGridSelection(index === 0 ? "GridDados" : "GridArquivos")
              }
            />
          ))}
        </List>
      </Drawer>

      <Box component="main" className="main-container">
        <Toolbar />
        {selectedGrid === "dados" ? <GridDados /> : <GridArquivos />}
      </Box>
    </>
  );
}
