import React, { useState } from "react";
import Box from "@mui/material/Box";
import Button from "@mui/material/Button";
import Snackbar from "@mui/material/Snackbar";
import MuiAlert from "@mui/material/Alert";
import CircularProgress from "@mui/material/CircularProgress";
import "./Styles/content.scss";

export default function BulkFileXml() {
  const [selectedMethod, setSelectedMethod] = useState("file");
  const [files, setFiles] = useState([]);
  const [dragState, setDragState] = useState(false);
  const [snackbarOpen, setSnackbarOpen] = useState(false);
  const [dropMsg, setDropMsg] = useState(
    "Arraste o arquivo nesta área ou clique para fazer upload"
  );

  // const dropFileUpload = (e) => {
  //   e.preventDefault();
  //   const files = e.dataTransfer.files;
  //   const reader = new FileReader();
  //   reader.onload = function (e) {
  //     const xmlContent = e.target.result;
  //     const blob = new Blob([xmlContent], { type: "text/xml" });
  //     handleSave(blob);
  //   };
  //   reader.readAsText(files[0]);
  // };

  const dropFileUpload = (e) => {
    e.preventDefault();
    const files = e.dataTransfer.files;
    const blobs = [];

    // Loop através de todos os arquivos e criar blobs para cada um deles
    for (let i = 0; i < files.length; i++) {
      const reader = new FileReader();
      reader.onload = function (event) {
        const xmlContent = event.target.result;
        const blob = new Blob([xmlContent], { type: "text/xml" });
        blobs.push(blob);

        // Se este for o último arquivo, chame handleSave com todos os blobs
        if (i === files.length - 1) {
          handleSave(blobs);
        }
      };
      reader.readAsText(files[i]);
    }
  };
  const handleDragEnter = (e) => {
    setDragState(true);
  };
  const handleDragLeave = (e) => {
    setDragState(false);
  };
  const handleCloseSnackbar = (event, reasson) => {
    setSnackbarOpen(false);
  };

  const traditionalFileUpload = (e) => {
    const selectedFiles = Array.from(e.target.files);
    setFiles(selectedFiles);
  };

  const handleSave = (blobs) => {
    const formData = new FormData();

    blobs.forEach((blob, index) => {
      const reader = new FileReader();
      reader.onload = function (event) {
        const xmlContent = event.target.result;
        formData.append(`file_${index}`, blob);
        if (index === blobs.length - 1) {
          // Quando todos os arquivos forem processados, envie a solicitação
          uploadFormData(formData);
        }
      };
      reader.readAsText(blob); // Lê o blob como texto
    });
  };

  const uploadFormData = (formData) => {
    fetch("http://127.0.0.1:8080/processarxml", {
      method: "POST",
      body: formData,
    })
      .then((response) => {
        if (!response.ok) {
          throw new Error("Failed to save XML");
        }
        return response.json(); // Se necessário, manipule a resposta do servidor
      })
      .then((data) => {
        console.log("XMLs enviados com sucesso:", data);
        setSnackbarOpen(true);
        setFiles([]);
      })
      .catch((error) => {
        console.error("Error saving XML:", error);
      });
  };
  // const handleSave = (blob) => {
  //   const reader = new FileReader();
  //   setTimeout(() => {
  //     setSnackbarOpen(true);
  //     setFiles([]);
  //   }, 2000);

  //   reader.onload = function (event) {
  //     const xmlContent = event.target.result;
  //     console.log(xmlContent);
  //     fetch("http://127.0.0.1:8080/processarxml", {
  //       method: "POST",
  //       headers: {
  //         Accept: "application/xml",
  //         "Content-Type": "application/xml",
  //       },
  //       body: xmlContent,
  //     })
  //       .then((response) => {
  //         if (!response.ok) {
  //           throw new Error("Failed to save XML");
  //         }
  //       })
  //       .catch((error) => {
  //         console.error("Error saving XML:", error);
  //       });
  //   };

  //   reader.readAsText(blob); // Lê o arquivo XML como texto
  // };

  const _textContent = (value) => {};
  const dragOverHandler = (e) => {};

  return (
    <>
      <div className="container">
        <div
          onDrop={(e) => dropFileUpload(e)}
          onDragOver={(e) => dragOverHandler(e)}
          onDragEnter={(e) => handleDragEnter(e)}
          onDragLeave={(e) => handleDragLeave(e)}
          className="content-zone"
        >
          {selectedMethod === "file" && (
            <div className={`info ${dragState && "drop-enabled"}`}>
              <div className="drop-or-upload">
                <h3>
                  <label htmlFor="fileUpload" className="custom-link">
                    <input
                      id="fileUpload"
                      type="file"
                      hidden
                      accept="application/xml"
                      onChange={(e) => traditionalFileUpload(e)}
                      multiple
                    />
                    {dropMsg}
                  </label>
                </h3>
              </div>
              {files && files.length > 0 && (
                <small className="file-name">
                  <>
                    Arquivo(s) selecionado(s):
                    <ul>
                      {files.map((f, i) => {
                        return <li key={i}>{f.name}</li>;
                      })}
                    </ul>
                  </>
                </small>
              )}
            </div>
          )}
          {selectedMethod === "text" && (
            <>
              <textarea
                className="text-box"
                placeholder="Cole o conteúdo do XML aqui"
                onChange={(e) => _textContent(e.target.value)}
              />
            </>
          )}
        </div>
      </div>
      <Box className="buttonContainer">
        <Button
          variant="contained"
          color="primary"
          onClick={() => handleSave(files[0])}
        >
          Salvar
        </Button>
      </Box>
      {/* {uploadSuccess && (
        <div className="success-message">Upload bem-sucedido!</div>
      )} */}
      <Snackbar
        open={snackbarOpen}
        autoHideDuration={6000}
        onClose={handleCloseSnackbar}
        anchorOrigin={{ vertical: "top", horizontal: "center" }}
      >
        <MuiAlert
          elevation={6}
          variant="filled"
          onClose={handleCloseSnackbar}
          severity="success"
        >
          Upload bem-sucedido!
        </MuiAlert>
      </Snackbar>
    </>
  );
}
