import React, { useState } from "react";
import { BrowserRouter as Router, Routes, Route, Link } from "react-router-dom";
import Drawer from "@mui/material/Drawer";
import Toolbar from "@mui/material/Toolbar";
import List from "@mui/material/List";
import Divider from "@mui/material/Divider";
import ListItemComponent from "./ListItemComponent";
import Box from "@mui/material/Box";
import GridDados from "../Grids/GridDados";
import GridArquivos from "../Grids/GridArquivos";
import "./Sidebar.scss";

export default function DrawerComponent() {
  const [showGrid, setShowGrid] = useState("dados");

  const grids = {
    dados: "Visualizar dados XML",
    arquivos: "Visualizar arquivo",
  };

  const handleListItemClick = (grid) => {
    console.log("Item clicado:", grid);
    setShowGrid(grid);
  };

  return (
    <Router>
      <Drawer
        className="muiDrawer-root & .muiDrawer-paper"
        variant="permanent"
        anchor="left"
      >
        <Toolbar />
        <Divider />
        <List>
          {Object.entries(grids).map(([grid, text], index) => (
            <ListItemComponent
              key={grid}
              text={text}
              index={index}
              onClick={() => handleListItemClick(grid)}
              to={`/${grid}`} // Adicionando a rota como propriedade
            />
          ))}
        </List>
      </Drawer>

      <Box component="main" className="main-container">
        <Toolbar />
        <Routes>
          <Route path="/dados" element={<GridDados />} />
          <Route path="/arquivos" element={<GridArquivos />} />
        </Routes>
      </Box>
    </Router>
  );
}
