import React from "react";
import Drawer from "@mui/material/Drawer";
import Toolbar from "@mui/material/Toolbar";
import List from "@mui/material/List";
import Divider from "@mui/material/Divider";
import ListItemComponent from "./ListItemComponent";
import Typography from "@mui/material/Typography";
import Box from "@mui/material/Box";
import Grid from "../Grids/Grid";
export default function DrawerComponent() {
  const drawerWidth = 240;
  return (
    <>
      <Drawer
        sx={{
          width: drawerWidth,
          flexShrink: 0,
          "& .MuiDrawer-paper": {
            width: drawerWidth,
            boxSizing: "border-box",
          },
        }}
        variant="permanent"
        anchor="left"
      >
        <Toolbar />
        <Divider />
        <List>
          {["Visualizar dados XML", "Visualizar arquivo"].map((text, index) => (
            <ListItemComponent key={text} text={text} index={index} />
          ))}
        </List>
      </Drawer>

      <Box
        component="main"
        sx={{
          flexGrow: 1,
          bgcolor: "background.default",
          p: 3,
          overflowX: "auto", // Para permitir rolagem horizontal se a grade for maior que o box
          maxWidth: `calc(100% - ${drawerWidth}px)`, // Definir a largura máxima do box
          marginLeft: `${drawerWidth}px`, // Adicionar margem à esquerda para compensar a largura do drawer
        }}
      >
        <Toolbar />
        <Grid />
      </Box>
    </>
  );
}
