import React, { useState } from "react";
import Drawer from "@mui/material/Drawer";
import Toolbar from "@mui/material/Toolbar";
import List from "@mui/material/List";
import Divider from "@mui/material/Divider";
import ListItemComponent from "./ListItemComponent";
import Box from "@mui/material/Box";
import GridDados from "../Grids/GridDados";
import GridArquivos from "../Grids/GridArquivos";
import "./Sidebar.scss";
export default function DrawerComponent() {
  const [selectedItem, setSelectedItem] = useState("visualizarDadosXML");

  const itemComponents = {
    visualizarDadosXML: <GridDados />,
    visualizarArquivo: <GridArquivos />,
  };

  const handleItemClick = (itemKey) => {
    setSelectedItem(itemKey);
  };
  return (
    <>
      <Drawer
        className="muiDrawer-root & .muiDrawer-paper"
        variant="permanent"
        anchor="left"
      >
        <Toolbar />
        <Divider />
        <List>
          {[
            { key: "visualizarDadosXML", text: "Visualizar dados XML" },
            { key: "visualizarArquivo", text: "Visualizar arquivo" },
          ].map(({ key, text }, index) => (
            <NavLink
              key={key}
              to={`/${key}`}
              onClick={() => handleItemClick(key)}
            >
              <ListItemComponent text={text} index={index} />
            </NavLink>
          ))}
        </List>
      </Drawer>

      <Box component="main" className="main-container">
        <Toolbar />
        {showGrid === "dados" && <GridDados />}
        {showGrid === "arquivos" && <GridArquivos />}
      </Box>
    </>
  );
}
